from django.shortcuts import render, redirect
from django.http import HttpResponse
from .models import Meetup, Participant
from .forms import RegistrationForm

# Create your views here.
# request paramaeter automaticall generated by Django


def index(request):

    # return HttpResponse('Hello World!')
    # now we have to return html file as response

    # dynamic data show on template
    meetups = Meetup.objects.all()
    return render(request, 'meetups/index.html', {
        'meetups': meetups,
        'show_meetups': False
    })


def meetup_details(request, meetup_slug):
    print('meetup_details called')
    try:
        selected_meetup = Meetup.objects.get(slug=meetup_slug)
        print(f'selected meetup{selected_meetup}')
        if request.method == 'GET':
            registration_form = RegistrationForm()

        else:
            registration_form = RegistrationForm(request.POST)
            if registration_form.is_valid():
                user_email = registration_form.cleaned_data['email']
                participant, _ = Participant.objects.get_or_create(
                    email=user_email)

                # participant = registration_form.save()
                selected_meetup.participant.add(participant)
                return redirect('confirm-registeration')

        return render(request, 'meetups/meetup-details.html',
                      {
                          'meetup_found': True,
                          'selected_meetup': selected_meetup,
                          'form': registration_form
                      })

    except Exception as exc:
        print(f'error occured : {exc}')
        return render(request, 'meetups/meetup-details.html', {
            'meetup_found': False
        })


def confirm_registeration(request):
    return render(request, 'meetups/registeration-success.html')
